@ECHO OFF

SETLOCAL ENABLEDELAYEDEXPANSION

IF NOT EXIST natisni\NUL (
    MKDIR natisni
)

FOR /F %%G IN (seznam.txt) DO (
    SET pattern="%%G.idw??.pdf"
    SET /A count=0
    FOR /F %%F IN ('DIR /B !pattern! 2^> NUL') DO (
        @COPY %%F natisni > NUL
        SET /A count+=1
    )
    IF !count! EQU 0 (
        @ECHO Nobena datoteka ne ustreza vzorcu !pattern!
    )
)

ENDLOCAL

