FOR /F %%G IN (seznam.txt) DO (
    SET pattern="%%G.idw??.pdf"
    FOR /F %%I IN ('DIR /B !pattern! 2^> NUL ^| FIND /C "."') DO SET count=%%I
    IF !count! GTR 0 (
        COPY !pattern! natisni > NUL
    ) ELSE (
        @ECHO Nobena datoteka ne ustreza vzorcu !pattern!
    )
)

ENDLOCAL

