Živjo mene zanima kaj je narobe z tem mojim programom pri sosrtiranju mi kaže v vskai vrstici napaka..ce mi lahko kdo pove napiše pa sbscripted value is neither array nor pointer..:)tudi če poveste samo kje naj bi bili napaka :)   #include <stdio.h>
#include <stdlib.h>
void papa();
struct delavec
{
char ime_osebe[35];
char kraj_rojstva[35];
int letnica_rojstva;
};

int main(int argc, char *argv[])
{struct delavec x;
FILE *fp;
char c;
fp=fopen("garac.bin","wb");
do
{
fflush(stdin);
gets(x.ime_osebe);
fflush(stdin);
gets(x.kraj_rojstva);
scanf("%d",&x.letnica_rojstva);
fwrite(&x,sizeof(x),1,fp);
printf("vpisi bi se");
fflush(stdin);
scanf("%c",&c);
}
while(c=='d');
fclose(fp);
papa();
  system("PAUSE");	
  return 0;
}
void papa()
{struct delavec x;
FILE *fp;
char c;
int i=0,j,k;int f;char l[35];
fp=fopen("garac.bin","rb");
while(fread(&x,sizeof(x),1,fp)==1)
{i++;}
for(j=0;j<10;j++)
for(k=9;k>j;k--)
if(x[k].letnica_rojstva>x[k-1].letnica_rojstva)
{f=x[k].letnica_rojstva;
x[k].letnica_rojstva=x[k-1].letnica_rojstva;
x[k].letnica_rojstva=f;
strcpy(l,x[k].ime_osebe);
strcpy(x[k].ime_osebe,x[k-1].ime_osebe);
strcpy(x[k-1].ime_osebe,l);
strcpy(l,x[k].kraj_rojstva);
strcpy(x[k].kraj_rojstva,x[k-1].kraj_rojstva);
strcpy(x[k-1].kraj_rojstva,l);}

for(j=0;j<i;j++)
printf("%s  %s  %d",x[j].kraj_rojstva,x[j].ime_osebe,x[j].letnica_rojstva);
 fclose(fp);    
}
