	public int fak(int n) {

		int rezultat;

		if(n == 1) {
			return 1;
		}

		else {
			rezultat = n * fak(n-1);
			return rezultat;
		}
	} Tisto moje mišljenje se ne bo izšlo preveč dobro, ker se ob spremembi vrstice spremeni tudi število v stolpcu in je to potem začaran krog....

